mapper.insert:
INSERT INTO tbl_board (
	title,
	content,
	writer
) VALUE (
	#{title},
	#{content},
	#{writer}
)

mapper.update:
update tbl_board set
	title = #{title},
	content = #{content},
	writer = #{writer}
where 1=1
and bno = #{bno}

mapper.delete:
delete from tbl_board 
where 1=1
and bno = #{bno}
